import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

// import { connect } from "react-redux";

// import { fetchTasks } from "../redux/actions/taskActions";
// import * as taskActions from "../redux/actions/taskActions";
// import { bindActionCreators } from "redux";

const TaskList = props => {
  const { tasks } = props;

  // load on mount
  useEffect(() => {
    props.actions.fetchTasks().catch(error => {
      alert("Loading tasks failed" + error);
    });
  }, [props.actions]);

  return (
    <table className="table">
      <thead>
        <tr>
          <th />
          <th>Title</th>
          <th>Body</th>
          <th>Category</th>
        </tr>
      </thead>
      <tbody>
        {tasks.map(task => {
          return (
            <tr key={task.id}>
              <td>
                <a
                  className="btn btn-light"
                  href={"http://localhost:3001/" + task.slug}
                >
                  Watch??
                </a>
              </td>
              <td>
                <Link to={"/task/" + tasks.slug}>{task.title}</Link>
              </td>
              <td>{task.body}</td>
              <td>{task.category}</td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

/* with functional components, don't need mapStateToProps, can just pass props into the 
component as an argument above
must specify the type of all props made available to this compnent
 */
TaskList.propTypes = {
  tasks: PropTypes.array.isRequired
};

export default TaskList;
