import * as types from "./actionTypes";
import * as taskApi from "../../api/taskApi";

// These are ACTION CREATORS for the task reducer (department) of the store

export function createTask(task) {
  return {
    type: types.CREATE_TASK,
    task: task // key used is referenced in reducer
  };
}

// returns entire array of tasks
export function fetchTasks() {
  return function(dispatch) {
    return taskApi
      .getTasks()
      .then(tasks => {
        // separate action creator for successfull api call
        dispatch({ type: types.FETCH_TASKS_SUCCESS, tasks: tasks });
      })
      .catch(error => {
        throw error;
      });
  };
}

export function saveTask(task) {
  return function(dispatch, getState) {
    return taskApi
      .saveTask(task)
      .then(savedTask => {
        task.id
          ? dispatch({ type: types.UPDATE_TASK_SUCCESS, savedTask })
          : dispatch({ type: types.CREATE_TASK_SUCCESS, savedTask });
      })
      .catch(error => {
        throw error;
      });
  };
}
