import { FETCH_TASKS, NEW_TASK } from "./actionTypes";

export const fetchTasks = () => dispatch => {
  fetch("localhost:3001/tasks")
    .then(res => res.json())
    .then(tasks =>
      dispatch({
        type: FETCH_TASKS,
        payload: tasks
      })
    );
};
