import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import * as taskActions from "../../redux/actions/taskActions";
import * as categoryActions from "../../redux/actions/categoryActions";
import { bindActionCreators } from "redux";
import TaskForm from "./TaskForm";
// import { newTask } from "../../../tools/mockData";
import newTask from "./newTask";

function ManageTaskPage({
  tasks,
  categories,
  fetchTasks,
  fetchCategories,
  ...props
}) {
  const [task, setTask] = useState({ ...props.task }); // set initial value to empty newTask

  useEffect(() => {
    if (tasks.length === 0) {
      fetchTasks().catch(error => {
        alert("Loading tasks failed" + error);
      });
    }

    if (categories.length === 0) {
      fetchCategories().catch(error => {
        alert("Loading categories failed" + error);
      });
    }
  }, [categories.length, fetchCategories, fetchTasks, tasks.length]); // empty dependency array = only run useEffect once

  return (
    <>
      <TaskForm task={task} errors={errors} categories={categories} />
    </>
  );
}

// which parts of the state (DEPARTMENTS) to expose this component via props
function mapStateToProps(state) {
  return {
    task: newTask,
    tasks: state.tasks,
    categories: state.categories
  };
}

/* must specify the type for each prop (from mapStateToProps above)
 */
ManageTaskPage.propTypes = {
  task: PropTypes.object.isRequired,
  tasks: PropTypes.array.isRequired,
  actions: PropTypes.object.isRequired,
  categories: PropTypes.array.isRequired
};

// which (forms) ACTOIN CREATORS are available to drop off to dispatch (form receiver/ bad mail man) on this component
function mapDispatchToProps(dispatch) {
  return {
    actions: {
      fetchTasks: bindActionCreators(taskActions.fetchTasks, dispatch),
      fetchCategories: bindActionCreators(
        categoryActions.fetchCategories,
        dispatch
      )
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ManageTaskPage);
