import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";

// Note, tasks within checklists are different and un-related to standalone tasks module

const ChecklistTaskView = props => {
  const [tasks, setTasks] = useState({ ...props.tasks }); // initialize to empty newCategory

  console.log(tasks);

  return (
    <table className="table">
      <thead>
        <tr>
          <th />
          <th>Task</th>
          <th>Body</th>
          <th>Category</th>
          <th />
        </tr>
      </thead>
      <tbody>
        {tasks.map(task => {
          return (
            <tr key={task.id}>
              <td></td>
              <td>{task.title}</td>
              <td>{task.body}</td>
              <td>{task.category}</td>
              <td>
                <button
                  className={
                    !task.completed
                      ? "btn btn-outline-success"
                      : "btn btn-success"
                  }
                  // onClick={() => onDeleteClick(checklist)}
                >
                  {!task.completed ? "Incomplete" : "Completed"}
                </button>
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

ChecklistTaskView.propTypes = {
  tasks: PropTypes.array.isRequired
  // onDeleteClick: PropTypes.func.isRequired
};

export default ChecklistTaskView;
