import React, { useEffect } from "react";
import PropTypes from "prop-types";

// Note, tasks within checklists are different and un-related to standalone tasks module

const ChecklistTaskView = props => {
  const { tasks } = props;

  // tasks.sort((a, b) => {
  //   // sort by: completed (false first), then timeOfCompletion
  //   return (
  //     (a.completed === b.completed ? 0 : a.completed ? 1 : -1) ||
  //     a.timeOfCompletion - b.timeOfCompletion
  //   );
  // });

  return (
    <table className="table">
      <thead>
        <tr>
          <th />
          <th>Task</th>
          <th>Body</th>
          <th>Category</th>
          <th />
        </tr>
      </thead>
      <tbody>
        {tasks.map(task => {
          return (
            <tr key={task.id}>
              <td></td>
              <td>{task.title}</td>
              <td>{task.body}</td>
              <td>{task.category}</td>
              <td>
                <button
                  className={
                    !task.completed
                      ? "btn btn-outline-success"
                      : "btn btn-success"
                  }
                  // onClick={() => onDeleteClick(checklist)}
                >
                  {!task.completed ? "Incomplete" : "Completed"}
                </button>
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};

ChecklistTaskView.propTypes = {
  tasks: PropTypes.array.isRequired
  // onDeleteClick: PropTypes.func.isRequired
};

export default ChecklistTaskView;
