/* THIS PAGE IS FOR EDITING AN EXISTING CHECKLIST!!!
This page will allow editing the title, 
editing the tasks (title, body and category for each)
deleting and and also adding new tasks to the checklist (minimum of 2 tasks) 
 */

import React from "react";
import PropTypes from "prop-types";
import TextInput from "../common/TextInput";
import SelectInput from "../common/SelectInput";

/* This form handles Adding and Editing an existing form based
on whether or not there is an id present
*/

const ChecklistForm = ({
  checklist,
  categories,
  onSave,
  onChange,
  saving = false,
  errors = {}
}) => {
  const tasks = [...checklist.tasks];

  return (
    <form onSubmit={onSave}>
      <h2>{checklist.id ? "Edit" : "Add"} Checklist</h2>
      {errors.onSave && (
        <div className="alert alert-danger" role="alert">
          {errors.onSave}
        </div>
      )}

      <TextInput
        name="title"
        label="Checklist Title"
        value={checklist.title}
        onChange={onChange}
        error={errors.title}
      />

      <h3>Tasks</h3>
      {tasks.map((task, index) => {
        return (
          <div key={task.id} style={{ marginLeft: 60 }}>
            <div>
              <h4># {index}</h4>
              <TextInput
                name="title"
                label="Task Title"
                value={task.title}
                onChange={event => onChange(event, index)}
                error={errors.title}
              />

              <TextInput
                name="body"
                label="Body"
                value={tasks[index].body}
                onChange={event => onChange(event, index)}
                error={errors.body}
              />

              <SelectInput
                name="category"
                label="Category"
                value={checklist.tasks[index].category}
                defaultOption="Select Category"
                options={categories.map(category => ({
                  value: category.name,
                  text: category.name
                }))}
                onChange={event => onChange(event, index)}
                error={errors.category}
              />
            </div>
          </div>
        );
      })}

      <button type="submit" disabled={saving} className="btn btn-primary">
        {saving ? "Saving..." : "Save"}
      </button>
    </form>
  );
};

ChecklistForm.propTypes = {
  categories: PropTypes.array.isRequired,
  checklist: PropTypes.object.isRequired,
  errors: PropTypes.object,
  onSave: PropTypes.func.isRequired,
  onChange: PropTypes.func.isRequired,
  saving: PropTypes.bool
};

export default ChecklistForm;
